import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';

export default function PostDetails() {
  const { id } = useParams(); // Access the route parameter
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const deletePost = () => {
    fetch(`http://localhost:3000/posts/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json', // Set your headers if needed
        // Add any other necessary headers
      },
      // You can include a body for some DELETE requests, but it's often not required
      // body: JSON.stringify({ /* any data if needed */ }),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      // Handle success if needed
      console.log('Item deleted successfully');
    })
    .catch(error => {
      // Handle error if any
      console.error('There was a problem deleting the item:', error);
    });
  }


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3000/posts');
        if (!response.ok) {
          throw new Error('Network response was not ok.');
        }
        const result = await response.json();
        const currentPost = result.posts.filter((e) => {
          if (e._id === id) {
            return e;
          } else {
            return error;
          }
        })
        setData(currentPost[0]);
      } catch (error) {
        setError(error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [error, id]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  const currentUser = JSON.parse(localStorage.getItem('currentUser'));

  return (
    <div className="post-details-container">
      
      <h2>{data.title}</h2>
      <h4>By: {data.author.username}</h4>
      <p>{data.content}</p>
      {(data.author.username === currentUser.userData.username) ?
        <div className="buttons-container">
          <button>Update</button>
          <button onClick={deletePost}>Delete</button>
        </div> :
        <></>
      }
    </div>
  );
}
