import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import '../style/style.css';
import { useNavigate } from "react-router-dom";

export default function UpdatePostForm() {
    const { id, commentid } = useParams(); // Access the route parameter
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const currentUserToken = localStorage.getItem('token');
    const [postData, setPostData] = useState({
        comment: '',
    });
    useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await fetch('http://localhost:3000/posts/'+id+'/comments');
            if (!response.ok) {
              throw new Error('Network response was not ok.');
            }
            const result = await response.json();
            const currentPost = result.comments.filter((e) => {
              if (e._id === commentid) {
                return e;
              } else {
                return error;
              }
            })
            setPostData(currentPost[0]);
          } catch (error) {
            setError(error);
          } finally {
            setIsLoading(false);
          }
        };
    
        fetchData();
      }, [error, id, commentid]);
    
      if (isLoading) {
        return <div>Loading...</div>;
      }
    
      if (error) {
        return <div>Error: {error.message}</div>;
      }

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setPostData({ ...postData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          const response = await fetch('http://localhost:3000/posts/'+id+'/comments/'+commentid, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
              Authorization: currentUserToken
            },
            body: JSON.stringify(postData),
          });
          if (!response.ok) {
            throw new Error('Network response was not ok.');
          }
          // Handle success if needed
          navigate("/posts/"+id);
        } catch (error) {
          console.error('Error posting data:', error);
          // Handle error state if needed
        }
      };

    return (

        <div class="form-container">
            <h1>Update Comment</h1>
                <form onSubmit={handleSubmit} class="log-in-container" action="/create-comment" method="POST">

                    <div class="label-input-container">
                        <label for="comment">Comment</label>
                        <textarea
                            id="comment"
                            name="comment"
                            rows="4"
                            cols="50"
                            onChange={handleInputChange}
                            value={postData.comment}
                        >
                        </textarea>
                    </div>

                    <button class="submit-button">
                        Submit
                    </button>

                </form>
        </div>	

    )
}
