import React, { useState } from "react";
import '../style/style.css';
import { useNavigate } from "react-router-dom";

export default function PostForm() {
    const navigate = useNavigate();
    const currentUserToken = localStorage.getItem('token');
    const [postData, setPostData] = useState({
        title: '',
        content: '',
    });
    
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setPostData({ ...postData, [name]: value });
    };

    const updatePost = async (e) => {
        e.preventDefault();
        try {
          const response = await fetch(`http://localhost:3000/posts/${id}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json', 
              Authorization: currentUserToken // Set your headers if needed
              // Add any other necessary headers
            },
            // You can include a body for some DELETE requests, but it's often not required
            // body: JSON.stringify({ /* any data if needed */ }),
          });
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          // Handle success if needed
          console.log('Item updated successfully');
          // navigate("/");
        } catch(error) {
          // Handle error if any
          console.error('There was a problem updating the item:', error);
        }
      };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          const response = await fetch('http://localhost:3000/posts', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              Authorization: currentUserToken
            },
            body: JSON.stringify(postData),
          });
          if (!response.ok) {
            throw new Error('Network response was not ok.');
          }
          // Handle success if needed
          navigate("/");
        } catch (error) {
          console.error('Error posting data:', error);
          // Handle error state if needed
        }
      };

    return (

        <div class="form-container">
            <h1>Create a Message</h1>
                <form onSubmit={handleSubmit} class="log-in-container" action="/create-message" method="POST">

                    <div class="log-in-container">
                        <label for="title">Post Title</label>
                        <input
                            name="title"
                            type="text"
                            onChange={handleInputChange}
                        />
                    </div>

                    <div class="label-input-container">
                        <label for="contetn">Post Content</label>
                        <textarea
                            id="content"
                            name="content"
                            rows="4"
                            cols="50"
                            onChange={handleInputChange}
                        >
                        </textarea>
                    </div>

                    <button class="submit-button">
                        Submit
                    </button>

                </form>
        </div>	

    )
}
